import React from "react";


const Form = ({input, setInput, todos,setTodos }) => {
    const onInputChange = (event)=>{
        setInput(event.target.value);
    }

    const onFormSubmit = (event)=>{
        event.preventDefault();
        setTodos([...todos,{id: Math.floor((Math.random() * 100000)), title: input, completed:false }])
        setInput("");
    }

    return (
        <form>
            <input type="text" placeholder="Enter a todo task..." className="task-input" value={input} required onChange={onInputChange}/>
            <button className="button-add" type="submit">Add </button>
        </form>
    )
}

export default Form;
